import { Module, Type, type } from "arktype";
import { AI_STATE } from "./state";
import { Schemas, getJsonSchema } from "./schemas";
import type { ServerConfig } from "../config";

export interface AIRunOptions {
  prompt: string;
  schema: type.Any;
  config: ServerConfig;
  taskName: string;
  maxTokens?: number;
  temperature?: number;
}

export async function executeAITask<T>(
  options: AIRunOptions
): Promise<T | null> {
  const { session, isAiBusy, llama } = AI_STATE;
  const { prompt, schema, config, taskName, maxTokens = 400, temperature = 0.5 } = options;

  if (!session || isAiBusy || !llama) return null;

  AI_STATE.isAiBusy = true;
  const startTime = performance.now();
  if (config.logPrompts) {
      console.info(`[AI] <<< ${taskName} PROMPT (${prompt}s):\n`);
  }
  try {
    const grammar = await llama.createGrammarForJsonSchema(schema.toJsonSchema());

    const response = await session.prompt(prompt, {
      maxTokens,
      temperature,
      grammar
    });

    const duration = ((performance.now() - startTime) / 1000).toFixed(2);
    if (config.logPrompts) {
      console.log(`[AI] <<< ${taskName} RESPONSE (${duration}s):\n${response}`);
    }

    // Parse response string back to object
    const data = typeof response === "string" ? JSON.parse(response) : response;
    const result = schema(data);

    if (result.problems) {
      console.error(`[AI] ${taskName} validation failed:`, result.problems.summary);
      return null;
    }

    return result.data || result;
  } catch (e) {
    console.error(`[AI] ${taskName} execution failed:`, e);
    return null;
  } finally {
    AI_STATE.isAiBusy = false;
  }
}

export * from "./types";
export * from "./state";
export * from "./methods";

import { executeAITask } from "./core";
import { Schemas } from "./schemas";
import { 
  DESCRIBE_PROMPT, 
  NEW_CONNECTIONS_PROMPT, 
  FIND_CONNECTIONS_PROMPT, 
  CREATE_SVG_PROMPT
} from "./prompts";
import type { ServerConfig } from "../config";
import type { GraphNode } from "../../src/types/graph";

export interface NewConnectionsParams {
  label: string;
  forbiddenNodes: string[];
  aspectList: string[];
  existingNodes: string[];
  creativity: number;
  config: ServerConfig;
}

export async function newConnections(params: NewConnectionsParams): Promise<typeof Schemas.ConnectionResponse.infer> {
  const { label, existingNodes, creativity, config, aspectList } = params;
  const existingStr = existingNodes.join(", ");

  const prompt = NEW_CONNECTIONS_PROMPT(label, existingStr, aspectList.join(", "));

  const result = await executeAITask<typeof Schemas.ConnectionResponse.infer>({
    prompt,
    schema: Schemas.ConnectionResponse,
    config,
    taskName: `NEW_CONNECTIONS`,
    temperature: creativity,
    maxTokens: 1000
  });

  return result || { connections: [] };
}

export interface FindConnectionsParams {
  label: string;
  existingNodes: string[];
  config: ServerConfig;
}

export async function findExistingConnections(params: FindConnectionsParams): Promise<typeof Schemas.ConnectionResponse.infer> {
  const { label, existingNodes, config } = params;
  const existingStr = existingNodes.join(", ");

  const prompt = FIND_CONNECTIONS_PROMPT(label, existingStr);

  const result = await executeAITask<typeof Schemas.ConnectionResponse.infer>({
    prompt,
    schema: Schemas.ConnectionResponse,
    config,
    taskName: `FIND_EXISTING_CONNECTIONS`,
    temperature: 0.3,
    maxTokens: 1000
  });

  return result || { connections: [] };
}

export async function describeNode(
  label: string, 
  aspectList: string[], 
  config: ServerConfig
): Promise<{ description: string; aspects: Record<string, number>, color: string, shape: string } | null> {

  const result = await executeAITask<typeof Schemas.Node.infer>({
    prompt: DESCRIBE_PROMPT(label, aspectList.join(", ")),
    schema: Schemas.Node,
    config,
    taskName: "EVALUATE_ASPECTS",
    temperature: 0.1,
    maxTokens: 500
  });

  if (!result) return null;

  const aspects: Record<string, number> = {};
  if (Array.isArray(result.scores)) {
    result.scores.forEach((s: any) => {
      aspects[s.aspect] = s.rating;
    });
  }

  return {
    description: result.description,
    aspects,
    color: result.color || "white",
    shape: result.shape || "generic"
  };
}

export async function generateSvg(concept: GraphNode, config: ServerConfig): Promise<string> {
  const result = await executeAITask<string>({
    prompt: CREATE_SVG_PROMPT(concept),
    schema: Schemas.SvgResponse,
    config,
    taskName: "CREATE_SVG",
    temperature: 0.1,
    maxTokens: 1000
  });
  return result || "";
}

import { Schemas } from "./schemas";
import type { Operation } from "fast-json-patch";
import type { GraphData, GraphLink, GraphNode, GraphState } from "../../src/types/graph";

export function shortRandomHash(length: 6 | 8 = 8): string {
  const bytesLen = Math.ceil((length * 3) / 4);
  const bytes = new Uint8Array(bytesLen);
  crypto.getRandomValues(bytes);

  return Buffer.from(bytes).toString("base64url").slice(0, length);
}
/**
 * Tworzy patch (fast-json-patch) dodający nody i linki do GraphData.
 * Każdy input node zostanie połączony linkiem: source=rootId -> target=newNodeId.
 */
export function aiListToGraphPatch(
  rootId: string,
  state: GraphState,
  input: typeof Schemas.ConnectionResponse.infer,
): Operation[] {


  const ops: Operation[] = [];

  for (const n of input.connections  ?? []) {
    const nodeId = shortRandomHash();

    const graphNode: GraphNode = {
      id: nodeId,
      label: n.target,
      status: 'accepted',
      type: "concept",
      val: 1,
      aspects: {},
    };

    const linkId = shortRandomHash();

    const graphLink: GraphLink = {
      id: linkId,
      source: rootId,
      target: nodeId,
      type: "ai",
      relationType: n.relation
    };

    ops.push({ op: "add", path: `/nodes/${nodeId}`, value: graphNode });
    ops.push({ op: "add", path: `/links/-`, value: graphLink });
  }

  return ops;
}

import { type } from "arktype";
import { Connect } from "vite";

// const Relations = "'enables' | 'causes' | 'conflicts_with' | 'depends_on' | 'example_of' | 'part_of' | 'risk_of' | 'opportunity_for' | 'similar_to' | 'opposite_of' | 'subclass_of' | 'instance_of' | 'incompatible_with'";

export const Relations = type.module({
    hierarchical: `'subclass_of'`,

    structural: `
        'part_of' |
        'has_part'
    `,

    causal: `
        'causes' |
        'enables' |
        'depends_on'
    `,

    logical: `
        'incompatible_with' |
        'similar_to' |
        'opposite_of'
    `,

    functional: `
        causal
    `,

    all: `
        hierarchical |
        structural |
        causal |
        logical
    `
})

export const Schemas = type.module({
    Node: {
        label: "string",
        scores: "AspectRating[]",
        description: "string",
        "color?": "string",
        "shape?": "string"
    },
    Connection: {
        target: "string",
        relation: Relations.all
    },
    ConnectionResponse: {
        connections: "Connection[]",
    },
    AspectResponse: "string[]",
    AspectRating: {
        aspect: "string",
        rating: "number"
    },
    SvgResponse: "string"
});

/**
 * Helper to convert Arktype schemas to JSON Schema for the LLM grammar
 */
export function getJsonSchema(arkType: any) {
    return arkType.toJsonSchema();
}

import { existsSync } from "fs";
import { getLlama, LlamaChatSession } from "node-llama-cpp";
import type { AIState } from "./types";
import type { ServerConfig } from "../config";
import { SYSTEM_PROMPT } from "./prompts";

export const AI_STATE: AIState = {
  llama: null,
  model: null,
  context: null,
  session: null,
  isAiBusy: false,
};

export async function unloadAI(): Promise<void> {
  console.log("[AI] Unloading model...");
  AI_STATE.session = null;
  AI_STATE.context = null;
  AI_STATE.model = null;
  AI_STATE.isAiBusy = false;
}

export async function initializeAI(
  config: ServerConfig,
  onReady?: (error?: string) => void
): Promise<void> {
  const path = config.modelPaths[config.selectedSize];

  if (AI_STATE.model) {
    await unloadAI();
  }

  if (!path || !existsSync(path)) {
    const err = `Model file not found: ${path}`;
    console.error(`[AI] ${err}`);
    onReady?.(err);
    return;
  }

  try {
    console.log(`\n--- [AI INITIALIZATION] size: ${config.selectedSize} ---`);
    if (!AI_STATE.llama) {
      AI_STATE.llama = await getLlama();
    }

    AI_STATE.model = await AI_STATE.llama.loadModel({ modelPath: path });
    if (!AI_STATE.model) throw new Error("Failed to load model");

    AI_STATE.context = await AI_STATE.model.createContext();
    AI_STATE.session = new LlamaChatSession({
      contextSequence: AI_STATE.context!.getSequence(),
      systemPrompt: SYSTEM_PROMPT
    });

    console.log("[AI] READY: Semantic Engine online.");
    onReady?.();
  } catch (e: any) {
    console.error("[AI] Load error", e);
    onReady?.(e.message || "Unknown error");
  }
}

export function getAIStatus(): 'unloaded' | 'ready' | 'loading' | 'error' {
    if (AI_STATE.model && AI_STATE.session) return 'ready';
    return 'unloaded';
}

import { LlamaModel, LlamaContext, LlamaChatSession } from "node-llama-cpp";

export interface AIState {
  llama: any;
  model: LlamaModel | null;
  context: LlamaContext | null;
  session: LlamaChatSession | null;
  isAiBusy: boolean;
}

import type { GraphState, GraphNode } from "../src/types/graph";
import { AI_STATE, newConnections, describeNode, generateSvg } from "./ai/index";
import { addAIGeneratedNodes, applyStatePatches } from "./state";
import type { Operation } from "fast-json-patch";
import type { ServerConfig } from "./config";
import { create } from "domain";

export interface AutoExploreContext {
  state: GraphState;
  config: ServerConfig;
  broadcast: (payload: any) => void;
  triggerSave: () => void;
}

let autoExploreInterval: NodeJS.Timeout | null = null;

export function startAutoExplore(context: AutoExploreContext): NodeJS.Timeout {
  if (autoExploreInterval) clearInterval(autoExploreInterval);

  autoExploreInterval = setInterval(async () => {
    await runAutoExploreIteration(context);
  }, 100);

  return autoExploreInterval;
}

export function stopAutoExplore(): void {
  if (autoExploreInterval) {
    clearInterval(autoExploreInterval);
    autoExploreInterval = null;
  }
}

async function runAutoExploreIteration(context: AutoExploreContext): Promise<void> {
  const { state, config, broadcast, triggerSave } = context;

  // Exit if AI is busy or model not loaded
  if (AI_STATE.isAiBusy || !AI_STATE.model) return;

  const needsMetadata = (n: GraphNode) => n.status === "accepted" && (!n.description || !n.aspects || Object.keys(n.aspects).length === 0);
  const needsSvg = (n: GraphNode) => n.status === "accepted" && n.description && !n.svg;
  const needsConnections = (n: GraphNode) => {
    if (n.status !== "accepted" && !state.settings.autoExplore) return false;
    const links = state.links.filter(l => l.source === n.id || l.target === n.id);
    return links.length < state.settings.minConnections;
  };

  const allNodes = Object.values(state.nodes);
  let targetNode = null;

  // PRIORITY 1: Metadata Enrichment (Clean up the graph first)
  targetNode = allNodes.find(needsMetadata);

  // PRIORITY 2: SVG Generation
  targetNode = allNodes.find(needsMetadata);

  // PRIORITY 3: New Connections (Only if all accepted nodes are described AND autoExplore is enabled)
  targetNode = allNodes.find(needsConnections);

  if (!targetNode) return;

  // Check if we should DESCRIBE or EXPLORE
  const mode = needsMetadata(targetNode) ? 'DESCRIBE' : needsSvg(targetNode) ? 'SVG' : 'EXPLORE';

  // Set thinking state
  applyStatePatches(state, [
    { op: "replace", path: "/thinkingNodeId", value: targetNode.id },
  ]);
  broadcast({ type: "PATCH", patches: [{ op: "replace", path: "/thinkingNodeId", value: targetNode.id }] });

  if (mode === 'DESCRIBE') {
    const nodeInfo = await describeNode(targetNode.label, state.settings.definedAspects, config);

    // Clear thinking state
    const patches: Operation[] = [
      { op: "replace", path: "/thinkingNodeId", value: null }
    ];

    if (nodeInfo) {
      patches.push({ op: "replace", path: `/nodes/${targetNode.id}/description`, value: nodeInfo.description });
      patches.push({ op: "replace", path: `/nodes/${targetNode.id}/aspects`, value: nodeInfo.aspects });
      patches.push({ op: "replace", path: `/nodes/${targetNode.id}/color`, value: nodeInfo.color });
      patches.push({ op: "replace", path: `/nodes/${targetNode.id}/shape`, value: nodeInfo.shape });
    }

    applyStatePatches(state, patches);
    broadcast({ type: "PATCH", patches });
    triggerSave();
  } else if ( mode === 'SVG') {
    const nodeInfo = await generateSvg(targetNode.label, state.settings.definedAspects, config);

    // Clear thinking state
    const patches: Operation[] = [
      { op: "replace", path: "/thinkingNodeId", value: null }
    ];

    if (nodeInfo) {
      patches.push({ op: "replace", path: `/nodes/${targetNode.id}/svg`, value: nodeInfo.svg });
    }

    applyStatePatches(state, patches);
    broadcast({ type: "PATCH", patches });
    triggerSave();
  } else if (mode === 'EXPLORE') {

    // Generate connections
    const neighborIds = state.links
      .filter(l => l.source === targetNode.id || l.target === targetNode.id)
      .map(l => l.source === targetNode.id ? l.target : l.source);

    const contextLabels = Array.from(new Set(neighborIds))
      .map(id => state.nodes[id]?.label)
      .filter(Boolean)
      .slice(0, 10);

    const forbiddenLabels = Object.values(state.nodes)
      .filter(n => n.status === "forbidden")
      .map(n => n.label.toLowerCase());

    const suggestions = await newConnections({
      label: targetNode.label,
      forbiddenNodes: forbiddenLabels,
      existingNodes: contextLabels,
      creativity: state.settings.creativity,
      aspectList: state.settings.definedAspects,
      config
    });

    // Clear thinking and apply changes
    const patches: Operation[] = [
      { op: "replace", path: "/thinkingNodeId", value: null },
    ];

    applyStatePatches(state, patches);
    broadcast({ type: "PATCH", patches });

    if (suggestions && suggestions.connections.length > 0) {
      const ops = addAIGeneratedNodes(state, targetNode.id, suggestions);
      applyStatePatches(state, ops);
      broadcast({ type: "PATCH", patches: ops });
      triggerSave();
    }
  }
}

export interface ServerConfig {
  modelPaths: {
    small: string;
    medium: string;
    large: string;
  };
  selectedSize: 'small' | 'medium' | 'large';
  loadOnStartup: boolean;
  logPrompts: boolean;
}

export const DEFAULT_CONFIG: ServerConfig = {
  modelPaths: {
    small: "",
    medium: "",
    large: "",
  },
  selectedSize: 'medium',
  loadOnStartup: true,
  logPrompts: false,
};

export async function loadConfig(configPath: string): Promise<ServerConfig> {
  try {
    console.log("Loading config from", configPath);
    const confFile = Bun.file(configPath);
    if (await confFile.exists()) {
      const config = await confFile.json();
      console.log("[Config] Server config loaded.");
      return { ...DEFAULT_CONFIG, ...config };
    }
  } catch (e) {
    console.error("[Config] Failed to load config.json");
  }
  return DEFAULT_CONFIG;
}

import { serve } from "bun";
import type { GraphState } from "../src/types/graph";
import { join } from "path";
import { Schemas, getJsonSchema } from "./ai/schemas";

// Handle CLI Flags
const args = process.argv.slice(2);
if (args.includes("-d") || args.includes("--dump-schema")) {
  const schemas = {
    AspectList: getJsonSchema(Schemas.AspectResponse),
    Association: getJsonSchema(Schemas.ConnectionResponse),
    Eval: getJsonSchema(Schemas.Node)
  };
  console.log(JSON.stringify(schemas, null, 2));
  process.exit(0);
}

// Module imports
import { loadConfig } from "./config";
import {
  initializeDataDirectory,
  DATA_DIR,
  STATE_PATH,
  loadStateFromDisk,
  triggerDebouncedSave,
} from "./persistence";
import {
  createDefaultState,
  initializeLoadedState,
} from "./state";
import { initializeAI, AI_STATE, getAIStatus } from "./ai/index";
import { startAutoExplore } from "./auto-explore";
import {
  broadcast,
  createWSMessageHandler,
} from "./ws-handlers";

console.log("\n--- [STARTING BRAIN SERVER S2] ---");

const CONFIG_PATH = join(import.meta.dir, "./..", "config.json");
const clients = new Set<any>();

let state: GraphState;
let serverConfig = await loadConfig(CONFIG_PATH);

// Initialize state
initializeDataDirectory();

const loadedState = await loadStateFromDisk(STATE_PATH);
if (loadedState) {
  state = initializeLoadedState(loadedState);
  console.log("[State] Graph state loaded from disk.");
} else {
  state = createDefaultState();
  console.log("[State] Created new graph state.");
}

// Initialize AI if startup flag is set
if (serverConfig.loadOnStartup) {
  initializeAI(serverConfig, () => {
    broadcast(clients, { type: "AI_STATUS", status: "ready", size: serverConfig.selectedSize });
  });
}

// Create handler context
const handlerContext = {
  state,
  config: serverConfig,
  configPath: CONFIG_PATH,
  statePath: STATE_PATH,
  broadcast: (payload: any) => broadcast(clients, payload),
};

// Start the background auto-exploration agent
startAutoExplore({
  state,
  config: serverConfig,
  broadcast: (payload: any) => broadcast(clients, payload),
  triggerSave: () => triggerDebouncedSave(STATE_PATH, state),
});

// Start HTTP/WebSocket server
serve({
  port: 3001,
  hostname: "0.0.0.0",
  fetch(req, server) {
    if (server.upgrade(req)) return;
    return new Response("Brain Server S2 Online");
  },
  websocket: {
    open(ws) {
      clients.add(ws);
      // Initial sync for the client
      ws.send(JSON.stringify({ type: "FULL_STATE", state }));
      ws.send(JSON.stringify({ type: "AI_CONFIG_UPDATED", config: serverConfig }));
      ws.send(JSON.stringify({ type: "AI_STATUS", status: getAIStatus() }));
      console.log(`[WS] Client linked. Count: ${clients.size}`);
    },
    message: createWSMessageHandler(handlerContext),
    close(ws) {
      clients.delete(ws);
    },
  },
});

import { join } from "path";
import { mkdirSync, existsSync } from "fs";

export const DATA_DIR = join(process.cwd(), "data");
export const STATE_PATH = join(DATA_DIR, "state.json");

export function initializeDataDirectory(): void {
  if (!existsSync(DATA_DIR)) {
    mkdirSync(DATA_DIR);
  }
}

export async function loadStateFromDisk(statePath: string): Promise<any> {
  try {
    const file = Bun.file(statePath);
    if (await file.exists()) {
      return await file.json();
    }
  } catch (e) {
    console.error("[Persistence] Failed to load state.json");
  }
  return null;
}

export async function saveStateToDisk(statePath: string, state: any): Promise<void> {
  await Bun.write(statePath, JSON.stringify(state, null, 2));
}

let saveTimeout: NodeJS.Timeout | null = null;

export function triggerDebouncedSave(
  statePath: string,
  state: any,
  delayMs: number = 1000
): void {
  if (saveTimeout) clearTimeout(saveTimeout);
  saveTimeout = setTimeout(() => {
    saveStateToDisk(statePath, state).catch(e => 
      console.error("[Persistence] Save error", e)
    );
  }, delayMs);
}

export function cancelPendingSave(): void {
  if (saveTimeout) {
    clearTimeout(saveTimeout);
    saveTimeout = null;
  }
}

import { applyPatch, type Operation } from "fast-json-patch";
import type { GraphState } from "../src/types/graph";
import type { Schemas } from "./ai/schemas";

export function createDefaultState(): GraphState {
  return {
    nodes: {},
    links: [],
    focusNodeId: null,
    thinkingNodeId: null,
    settings: {
      creativity: 0.7,
      maxWords: 3,
      minConnections: 3,
      autoExplore: false,
      definedAspects: [],
      activeAspects: [],
    },
  };
}

export function initializeLoadedState(state: GraphState): GraphState {
  state.thinkingNodeId = null;
  if (!state.settings.definedAspects) {
    state.settings.definedAspects = [];
  }
  if (!state.settings.activeAspects) {
    state.settings.activeAspects = [];
  }
  return state;
}

export function applyStatePatches(
  state: GraphState,
  patches: Operation[]
): void {
  applyPatch(state, patches);
}

export function addNodeToState(
  state: GraphState,
  label: string,
  parentId?: string
): { nodeId: string; ops: Operation[] } {
  const id = Math.random().toString(36).slice(2, 9);
  const isFirst = Object.keys(state.nodes).length === 0;
  const newNode = {
    id,
    label,
    status: "accepted" as const,
    type: isFirst ? ("root" as const) : ("concept" as const),
    val: isFirst ? 5 : 3,
    aspects: {} as Record<string, number>,
  };

  const ops: Operation[] = [{ op: "add", path: `/nodes/${id}`, value: newNode }];
  if (parentId) {
    ops.push({
      op: "add",
      path: "/links/-",
      value: { source: parentId, target: id, type: "user" },
    });
  }
  if (isFirst) {
    ops.push({ op: "replace", path: "/focusNodeId", value: id });
  }

  return { nodeId: id, ops };
}

export function deleteNodeFromState(state: GraphState, nodeId: string): Operation[] {
  const remainingLinks = state.links.filter(
    l => l.source !== nodeId && l.target !== nodeId
  );
  return [
    { op: "remove", path: `/nodes/${nodeId}` },
    { op: "replace", path: "/links", value: remainingLinks },
  ];
}

export function clearStateGraph(state: GraphState): void {
  state.nodes = {};
  state.links = [];
  state.focusNodeId = null;
  state.thinkingNodeId = null;
}
export function shortRandomHash(length: 6 | 8 = 8): string {
  const bytesLen = Math.ceil((length * 3) / 4);
  const bytes = new Uint8Array(bytesLen);
  crypto.getRandomValues(bytes);

  return Buffer.from(bytes).toString("base64url").slice(0, length);
}
export function addAIGeneratedNodes(
  state: GraphState,
  targetNodeId: string,
  suggestions: typeof Schemas.ConnectionResponse.infer
): Operation[] {
  const ops: Operation[] = [];
  const existingNodes = Object.values(state.nodes);

  suggestions.connections.forEach((s) => {
    // Normalize label for comparison
    const normalizedLabel = s.target.trim().toLowerCase();
    const existing = existingNodes.find(n => n.label.trim().toLowerCase() === normalizedLabel);

    if (existing) {
      // Check if link already exists
      const linkExists = state.links.some(l => 
        (l.source === targetNodeId && l.target === existing.id) ||
        (l.source === existing.id && l.target === targetNodeId)
      );

      if (!linkExists) {
        ops.push({
          op: "add",
          path: "/links/-",
          value: {
            source: targetNodeId,
            target: existing.id,
            type: "bridge",
            relationType: s.relation,
          },
        });
      }
    } else {
      // Add as new proposed node
      const id = shortRandomHash();
      ops.push({
        op: "add",
        path: `/nodes/${id}`,
        value: {
          id,
          label: s.target,
          status: "accepted",
          type: "concept",
          val: 2,
          aspects: {},
        },
      });
      ops.push({
        op: "add",
        path: "/links/-",
        value: {
          source: targetNodeId,
          target: id,
          type: "ai",
          relationType: s.relation,
        },
      });
    }
  });
  return ops;
}

import type { GraphState } from "../src/types/graph";
import type { ServerConfig } from "./config";
import { newConnections, findExistingConnections, describeNode, AI_STATE, initializeAI, unloadAI } from "./ai/index";
import {
  addNodeToState,
  applyStatePatches,
  deleteNodeFromState,
  clearStateGraph,
  addAIGeneratedNodes,
} from "./state";
import { triggerDebouncedSave } from "./persistence";
import type { Operation } from "fast-json-patch";

export interface WSHandlerContext {
  state: GraphState;
  config: ServerConfig;
  configPath: string;
  statePath: string;
  broadcast: (payload: any) => void;
}

export function broadcast(clients: Set<any>, payload: any): void {
  const msg = JSON.stringify(payload);
  clients.forEach(c => {
    try {
      c.send(msg);
    } catch (e) {
      // Client may be disconnected
    }
  });
}

export async function handleWSMessage(
  message: any,
  context: WSHandlerContext
): Promise<void> {
  const { state, config, configPath, statePath, broadcast } = context;
  const data = message;

  switch (data.type) {
    case "SET_FOCUS":
      applyStatePatches(state, [{ op: "replace", path: "/focusNodeId", value: data.nodeId }]);
      broadcast({ type: "PATCH", patches: [{ op: "replace", path: "/focusNodeId", value: data.nodeId }] });
      triggerDebouncedSave(statePath, state);
      break;

    case "UPDATE_SETTINGS":
      applyStatePatches(state, [{ op: "replace", path: "/settings", value: data.settings }]);
      broadcast({ type: "PATCH", patches: [{ op: "replace", path: "/settings", value: data.settings }] });
      triggerDebouncedSave(statePath, state);
      break;

    case "ADD_NODE": {
      const { nodeId, ops } = addNodeToState(state, data.label, data.parentId);
      applyStatePatches(state, ops);
      broadcast({ type: "PATCH", patches: ops });
      triggerDebouncedSave(statePath, state);
      break;
    }

    case "ACCEPT_NODE":
      applyStatePatches(state, [{ op: "replace", path: `/nodes/${data.nodeId}/status`, value: "accepted" }]);
      broadcast({ type: "PATCH", patches: [{ op: "replace", path: `/nodes/${data.nodeId}/status`, value: "accepted" }] });
      triggerDebouncedSave(statePath, state);
      break;

    case "FORBID_NODE":
      applyStatePatches(state, [{ op: "replace", path: `/nodes/${data.nodeId}/status`, value: "forbidden" }]);
      broadcast({ type: "PATCH", patches: [{ op: "replace", path: `/nodes/${data.nodeId}/status`, value: "forbidden" }] });
      triggerDebouncedSave(statePath, state);
      break;

    case "DELETE_NODE": {
      const ops = deleteNodeFromState(state, data.nodeId);
      applyStatePatches(state, ops);
      broadcast({ type: "PATCH", patches: ops });
      triggerDebouncedSave(statePath, state);
      break;
    }

    case "CLEAR_GRAPH":
      clearStateGraph(state);
      broadcast({ type: "FULL_STATE", state });
      triggerDebouncedSave(statePath, state);
      break;

    case "LOAD_AI_MODEL":
      if (AI_STATE.isAiBusy) return;
      broadcast({ type: "AI_STATUS", status: "loading" });
      await initializeAI(config, (err) => {
        if (err) broadcast({ type: "AI_STATUS", status: "error", error: err });
        else broadcast({ type: "AI_STATUS", status: "ready", size: config.selectedSize });
      });
      break;

    case "UNLOAD_AI_MODEL":
      if (AI_STATE.isAiBusy) return;
      await unloadAI();
      broadcast({ type: "AI_STATUS", status: "unloaded" });
      break;

    case "UPDATE_AI_CONFIG": {
      Object.assign(config, data.config);
      try {
        await Bun.write(configPath, JSON.stringify(config, null, 2));
        console.log("[Config] Server config updated.");
      } catch (e) {
        console.error("[Config] Failed to write config.json", e);
      }
      broadcast({ type: "AI_CONFIG_UPDATED", config });
      break;
    }

    case "EXPLORE_NEW":
    case "EXPLORE_EXISTING": {
      if (AI_STATE.isAiBusy || !AI_STATE.session) return;
      const targetNode = state.nodes[data.nodeId];
      if (!targetNode) return;

      applyStatePatches(state, [{ op: "replace", path: "/thinkingNodeId", value: targetNode.id }]);
      broadcast({ type: "PATCH", patches: [{ op: "replace", path: "/thinkingNodeId", value: targetNode.id }] });

      const neighborIds = state.links
        .filter(l => l.source === targetNode.id || l.target === targetNode.id)
        .map(l => l.source === targetNode.id ? l.target : l.source);

      const forbiddenLabels = Object.values(state.nodes)
        .filter(n => n.status === "forbidden")
        .map(n => n.label.toLowerCase());

      let suggestions;
      const contextLabels = Array.from(new Set(neighborIds))
        .map(id => state.nodes[id]?.label)
        .filter(Boolean)
        .slice(0, 10);

      if (data.type === "EXPLORE_NEW") {
        suggestions = await newConnections({
          label: targetNode.label,
          forbiddenNodes: forbiddenLabels,
          existingNodes: contextLabels,
          creativity: state.settings.creativity,
          aspectList: state.settings.definedAspects,
          config
        });
      } else {
        const candidates = Object.values(state.nodes)
          .filter(n => n.id !== targetNode.id && !neighborIds.includes(n.id) && n.status !== 'forbidden')
          .map(n => n.label)
          .slice(0, 15);

        suggestions = await findExistingConnections({
          label: targetNode.label,
          existingNodes: candidates,
          config
        });
      }

      applyStatePatches(state, [{ op: "replace", path: "/thinkingNodeId", value: null }]);
      broadcast({ type: "PATCH", patches: [{ op: "replace", path: "/thinkingNodeId", value: null }] });

      if (suggestions && suggestions.connections.length > 0) {
        const ops = addAIGeneratedNodes(state, targetNode.id, suggestions);
        applyStatePatches(state, ops);
        broadcast({ type: "PATCH", patches: ops });
        triggerDebouncedSave(statePath, state);
      }
      break;
    }

    case "UPDATE_NODE_ASPECTS": {
      if (AI_STATE.isAiBusy || !AI_STATE.session) return;
      const targetNode = state.nodes[data.nodeId];
      if (!targetNode) return;

      broadcast({ type: "PATCH", patches: [{ op: "replace", path: "/thinkingNodeId", value: targetNode.id }] });

      const nodeInfo = await describeNode(targetNode.label, state.settings.definedAspects, config);

      if (nodeInfo) {
        const patches: Operation[] = [
          { op: "replace", path: "/thinkingNodeId", value: null },
          { op: "replace", path: `/nodes/${targetNode.id}/description`, value: nodeInfo.description },
          { op: "replace", path: `/nodes/${targetNode.id}/aspects`, value: nodeInfo.aspects }
        ];
        applyStatePatches(state, patches);
        broadcast({ type: "PATCH", patches });
        triggerDebouncedSave(statePath, state);
      } else {
        broadcast({ type: "PATCH", patches: [{ op: "replace", path: "/thinkingNodeId", value: null }] });
      }
      break;
    }    
  }
}

export function createWSMessageHandler(context: WSHandlerContext) {
  return async (ws: any, message: any) => {
    try {
      const data = JSON.parse(message.toString());
      await handleWSMessage(data, context);

    } catch (e) {
      console.error("[WS] Message error", e);
    }
  };
}
